mapper.insert:
         
	insert into board(boa_snum, boa_subject, boa_regdate, mem_snum, boa_imgpng, boa_category)
		select seq_boa_snum.nextval, #{boa_subject}, sysdate, member.mem_snum, #{boa_imgpng}, #{boa_category}
		from member
		where member.mem_snum=#{mem_snum}      
   
   
   	select seq_boa_snum.currval from dual

mapper.select:
      
      select b.boa_snum boa_snum, b.boa_imgpng boa_imgpng, b.boa_subject boa_subject, b.boa_context boa_context, b.boa_regdate boa_regdate, m.mem_snum mem_snum, m.mem_id mem_id, m.mem_profile mem_profile, b.boa_category
		 from board b, member m
		 where b.mem_snum = m.mem_snum 
		 and m.mem_id = #{mem_id}
		 and b.boa_category = #{boa_category}
		 order by b.boa_snum desc, b.boa_regdate desc


	select 1 boa_snum
	from likelist
	where mem_snum=#{mem_snum} and boa_snum=#{boa_snum}

	
  		select tag_name tag
			from taglist
			where boa_snum= #{ boa_snum }
