mapper.select:

   select fri_snum
   from fri_list
   where mem_snum = #{mem_snum }
   	and fri_snum = #{fri_snum}


   select  f.mem_snum fri_mem_nsum, f.fri_snum, m.mem_id fri_mem_id, m.mem_profile fri_mem_profile 
   from fri_list f, member m
   where f.fri_mem_snum = m.mem_snum and f.mem_snum = #{memNo }
